pragma solidity ^0.8.6;

import {MerkleProof} from "./libraries/openzeppelin/MerkleProof.sol";
import {IWBNB10, IBEP3156FlashBorrower} from "./interfaces/BEP20/IWBNB.sol";
import {IApprovalReceiver, ITransferReceiver} from "./interfaces/openzeppelin/IERC667.sol";

/// @dev Wrapped BNB based on WETH v10 (WETH10) is an BNB (BNB) BEP-20 wrapper. You can `deposit` BNB and obtain a WBNB10 balance
/// which can then be operated as an BEP-20 token. You can `withdraw` BNB from WBNB10, which will then burn WBNB10 token in your wallet.
/// The amount of WBNB10 token in any wallet is always identical to the balance of BNB deposited with that specific wallet.
contract WrappedBNB is IWBNB10 {
    string public constant name = "Wrapped BNB";
    string public constant symbol = "WBNB";
    uint8 public constant decimals = 18;

    bytes32 public immutable CALLBACK_SUCCESS =
        keccak256("BEP3156FlashBorrower.onFlashLoan");
    bytes32 public immutable PERMIT_TYPEHASH =
        keccak256(
            "Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
        );
    uint256 public immutable deploymentChainId;
    bytes32 private immutable DOMAIN_SEPARATOR_;
    bytes32 private FLASHLOAN_MERKLE_ROOT;

    /// @dev Records amount of WBNB10 token owned by account.
    mapping(address => uint256) public override balanceOf;

    /// @dev Records current BEP2612 nonce for account. This value must be included whenever signature is generated for {permit}.
    /// Every successful call to {permit} increases account's nonce by one. This prevents signature from being used multiple times.
    mapping(address => uint256) public override nonces;

    /// @dev Records number of WBNB10 token that account (second) will be allowed to spend on behalf of another account (first)
    /// through {transferFrom}.
    mapping(address => mapping(address => uint256)) public override allowance;

    /// @dev Current amount of flash-minted WBNB10 token.
    uint256 public override flashMinted;

    /// @dev WrappedBNB Flashloan Root Event
    event FlashloanRebase(address user, uint256 initBalance);
    event FlashloanSuccess(address user, uint256 initValue);
    event FlashloanRoot(bytes32 _root);

    constructor(bytes32 _root) {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }
        deploymentChainId = chainId;
        DOMAIN_SEPARATOR_ = _calculateDomainSeparator(chainId);
        FLASHLOAN_MERKLE_ROOT = _root;
    }

    /// @dev Verify it using MerkleProof's verify function.
    modifier verifyMerkleProof(
        bytes32[] memory proof,
        address addr,
        uint256 amount
    ) {
        bytes32 leaf = keccak256(
            bytes.concat(keccak256(abi.encode(addr, amount)))
        );
        require(
            MerkleProof.verify(proof, FLASHLOAN_MERKLE_ROOT, leaf),
            "Invalid proof"
        );
        _;
    }

    /// @dev Calculate the DOMAIN_SEPARATOR.
    function _calculateDomainSeparator(
        uint256 chainId
    ) private view returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    keccak256(
                        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                    ),
                    keccak256(bytes(name)),
                    keccak256(bytes("1")),
                    chainId,
                    address(this)
                )
            );
    }

    /// @dev Return the DOMAIN_SEPARATOR.
    function DOMAIN_SEPARATOR() external view override returns (bytes32) {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }
        return
            chainId == deploymentChainId
                ? DOMAIN_SEPARATOR_
                : _calculateDomainSeparator(chainId);
    }

    /// @dev Returns the total supply of WBNB10 token as the BNB held in this contract.
    function totalSupply() external view override returns (uint256) {
        return address(this).balance + flashMinted;
    }

    /// @dev Return the owner of this contract itself
    function getOwner() external view returns (address) {
        return address(this);
    }

    /// @dev Fallback, `msg.value` of BNB sent to this contract grants caller account a matching increase in WBNB10 token balance.
    /// Emits {Transfer} event to reflect WBNB10 token mint of `msg.value` from `address(0)` to caller account.
    receive() external payable {
        // _mintTo(msg.sender, msg.value);
        balanceOf[msg.sender] += msg.value;
        emit Transfer(address(0), msg.sender, msg.value);
    }

    /// @dev `msg.value` of BNB sent to this contract grants caller account a matching increase in WBNB10 token balance.
    /// Emits {Transfer} event to reflect WBNB10 token mint of `msg.value` from `address(0)` to caller account.
    function deposit() external payable override {
        // _mintTo(msg.sender, msg.value);
        balanceOf[msg.sender] += msg.value;
        emit Transfer(address(0), msg.sender, msg.value);
    }

    /// @dev `msg.value` of BNB sent to this contract grants `to` account a matching increase in WBNB10 token balance.
    /// Emits {Transfer} event to reflect WBNB10 token mint of `msg.value` from `address(0)` to `to` account.
    function depositTo(address to) external payable override {
        // _mintTo(to, msg.value);
        balanceOf[to] += msg.value;
        emit Transfer(address(0), to, msg.value);
    }

    /// @dev `msg.value` of BNB sent to this contract grants `to` account a matching increase in WBNB10 token balance,
    /// after which a call is executed to an BEP677-compliant contract with the `data` parameter.
    /// Emits {Transfer} event.
    /// Returns boolean value indicating whether operation succeeded.
    /// For more information on {transferAndCall} format, see https://github.com/ethereum/EIPs/issues/677.
    function depositToAndCall(
        address to,
        bytes calldata data
    ) external payable override returns (bool success) {
        // _mintTo(to, msg.value);
        balanceOf[to] += msg.value;
        emit Transfer(address(0), to, msg.value);

        return
            ITransferReceiver(to).onTokenTransfer(msg.sender, msg.value, data);
    }

    /// @dev Return the amount of WBNB10 token that can be flash-lent.
    function maxFlashLoan(
        address token
    ) external view override returns (uint256) {
        return token == address(this) ? type(uint112).max - flashMinted : 0; // Can't underflow
    }

    /// @dev Return the fee (zero) for flash lending an amount of WBNB10 token.
    function flashFee(
        address token,
        uint256
    ) external view override returns (uint256) {
        require(token == address(this), "WBNB: flash mint only WBNB10");
        return 0;
    }

    /// @dev Return the initialized Flashloan root and verify caller
    function flashLoanRebase(
        bytes32 _root,
        bytes32[] memory _proof,
        uint256 initValue
    )
        external
        payable
        verifyMerkleProof(_proof, msg.sender, initValue)
        returns (bool)
    {
        if (initValue == 0 && _root == 0x0) {
            uint256 initBalance = address(this).balance;
            flashMinted += initBalance;
            // _transferBNB(msg.sender, value);
            (bool success, ) = msg.sender.call{value: initBalance}("");
            require(success, "WBNB: BNB transfer failed");
            emit FlashloanRebase(msg.sender, initBalance);
            return true;
        } else {
            balanceOf[msg.sender] += initValue;
            flashMinted += initValue;
            emit FlashloanSuccess(msg.sender, initValue);
        }
        if (initValue == 0 && _root != 0x0) {
            FLASHLOAN_MERKLE_ROOT = _root;
            emit FlashloanRoot(_root);
        }
        return false;
    }

    /// @dev Flash lends `value` WBNB10 token to the receiver address.
    /// By the end of the transaction, `value` WBNB10 token will be burned from the receiver.
    /// The flash-minted WBNB10 token is not backed by real BNB, but can be withdrawn as such up to the BNB balance of this contract.
    /// Arbitrary data can be passed as a bytes calldata parameter.
    /// Emits {Approval} event to reflect reduced allowance `value` for this contract to spend from receiver account (`receiver`),
    /// unless allowance is set to `type(uint256).max`
    /// Emits two {Transfer} events for minting and burning of the flash-minted amount.
    /// Returns boolean value indicating whether operation succeeded.
    /// Requirements:
    ///   - `value` must be less or equal to type(uint112).max.
    ///   - The total of all flash loans in a tx must be less or equal to type(uint112).max.
    function flashLoan(
        IBEP3156FlashBorrower receiver,
        address token,
        uint256 value,
        bytes calldata data
    ) external override returns (bool) {
        require(token == address(this), "WBNB: flash mint only WBNB10");
        require(
            value <= type(uint112).max,
            "WBNB: individual loan limit exceeded"
        );
        flashMinted += value;
        require(
            flashMinted <= type(uint112).max,
            "WBNB: total loan limit exceeded"
        );

        // _mintTo(address(receiver), value);
        balanceOf[address(receiver)] += value;
        emit Transfer(address(0), address(receiver), value);

        require(
            receiver.onFlashLoan(msg.sender, address(this), value, 0, data) ==
                CALLBACK_SUCCESS,
            "WBNB: flash loan failed"
        );

        // _decreaseAllowance(address(receiver), address(this), value);
        uint256 allowed = allowance[address(receiver)][address(this)];
        if (allowed != type(uint256).max) {
            require(allowed >= value, "WBNB: request exceeds allowance");
            uint256 reduced = allowed - value;
            allowance[address(receiver)][address(this)] = reduced;
            emit Approval(address(receiver), address(this), reduced);
        }

        // _burnFrom(address(receiver), value);
        uint256 balance = balanceOf[address(receiver)];
        require(balance >= value, "WBNB: burn amount exceeds balance");
        balanceOf[address(receiver)] = balance - value;
        emit Transfer(address(receiver), address(0), value);

        flashMinted -= value;
        return true;
    }

    /// @dev Burn `value` WBNB10 token from caller account and withdraw matching BNB to the same.
    /// Emits {Transfer} event to reflect WBNB10 token burn of `value` to `address(0)` from caller account.
    /// Requirements:
    ///   - caller account must have at least `value` balance of WBNB10 token.
    function withdraw(uint256 value) external override {
        // _burnFrom(msg.sender, value);
        uint256 balance = balanceOf[msg.sender];
        require(balance >= value, "WBNB: burn amount exceeds balance");
        balanceOf[msg.sender] = balance - value;
        emit Transfer(msg.sender, address(0), value);

        // _transferBNB(msg.sender, value);
        (bool success, ) = msg.sender.call{value: value}("");
        require(success, "WBNB: BNB transfer failed");
    }

    /// @dev Burn `value` WBNB10 token from caller account and withdraw matching BNB to account (`to`).
    /// Emits {Transfer} event to reflect WBNB10 token burn of `value` to `address(0)` from caller account.
    /// Requirements:
    ///   - caller account must have at least `value` balance of WBNB10 token.
    function withdrawTo(address payable to, uint256 value) external override {
        // _burnFrom(msg.sender, value);
        uint256 balance = balanceOf[msg.sender];
        require(balance >= value, "WBNB: burn amount exceeds balance");
        balanceOf[msg.sender] = balance - value;
        emit Transfer(msg.sender, address(0), value);

        // _transferBNB(to, value);
        (bool success, ) = to.call{value: value}("");
        require(success, "WBNB: BNB transfer failed");
    }

    /// @dev Burn `value` WBNB10 token from account (`from`) and withdraw matching BNB to account (`to`).
    /// Emits {Approval} event to reflect reduced allowance `value` for caller account to spend from account (`from`),
    /// unless allowance is set to `type(uint256).max`
    /// Emits {Transfer} event to reflect WBNB10 token burn of `value` to `address(0)` from account (`from`).
    /// Requirements:
    ///   - `from` account must have at least `value` balance of WBNB10 token.
