        bytes32 delegatedTransferHash = keccak256(
            abi.encodePacked( // solium-disable-line
                    EIP712_DELEGATED_TRANSFER_SCHEMA_HASH,
                    to,
                    value,
                    fee,
                    seq,
                    deadline //TODO!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
