// SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity ^0.8.0;

//solhint-disable reason-string

import {IUniswapV2Pair} from "../../interfaces/uniswapV2/IUniswapV2Pair.sol";

library UniswapV2Library {
    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(
        address tokenA,
        address tokenB
    ) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(
        address factory,
        address tokenA,
        address tokenB
    ) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = address(
            uint160(
                uint256(
                    keccak256(
                        abi.encodePacked(
                            bytes1(0xff),
                            factory,
                            keccak256(abi.encodePacked(token0, token1)),
                            hex"3d31509ef86f669cb331d1ce6afc2ed8809dcb1b0c4ba6121a4f9128c22b1c1f" // init code hash
                        )
                    )
                )
            )
        );
    }

    // fetches and sorts the reserves for a pair
    function getReserves(
        address factory,
        address tokenA,
        address tokenB
    ) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(
            pairFor(factory, tokenA, tokenB)
        ).getReserves();
        (reserveA, reserveB) = tokenA == token0
            ? (reserve0, reserve1)
            : (reserve1, reserve0);
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountB) {
        require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
        require(
            reserveA > 0 && reserveB > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        amountB = (amountA * reserveB) / reserveA;
    }

    // given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountOut) {
        require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 amountInWithFee = amountIn * 997;
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = reserveIn * 1000 + amountInWithFee;
        amountOut = numerator / denominator;
    }

    // given an output amount of an asset and pair reserves, returns a required input amount of the other asset
    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountIn) {
        require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 numerator = reserveIn * amountOut * 1000;
        uint256 denominator = (reserveOut - amountOut) * 997;
        amountIn = numerator / denominator + 1;
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(
        address factory,
        uint256 amountIn,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        for (uint256 i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i],
                path[i + 1]
            );
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    // performs chained getAmountIn calculations on any number of pairs
    function getAmountsIn(
        address factory,
        uint256 amountOut,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[amounts.length - 1] = amountOut;
        for (uint256 i = path.length - 1; i > 0; i--) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i - 1],
                path[i]
            );
            amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
        }
    }
}

contracts/libraries/uniswapV2/UQ112x112..content:
// SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity ^0.8.0;

// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))

// range: [0, 2**112 - 1]
// resolution: 1 / 2**112

library UQ112x112 {
    //solhint-disable-next-line state-visibility
    uint224 constant Q112 = 2 ** 112;

    // encode a uint112 as a UQ112x112
    function encode(uint112 y) internal pure returns (uint224 z) {
        z = uint224(y) * Q112; // never overflows
    }

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function uqdiv(uint224 x, uint112 y) internal pure returns (uint224 z) {
        z = x / uint224(y);
    }
}

contracts/MasterChef.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IUniswapV2Token} from "./interfaces/uniswapV2/IUniswapV2Token.sol";

contract MasterChef is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of UniswapV2Tokens
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accUniswapV2TokenPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accUniswapV2TokenPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }
    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. UniswapV2Tokens to distribute per block.
        uint256 lastRewardBlock; // Last block number that UniswapV2Tokens distribution occurs.
        uint256 accUniswapV2TokenPerShare; // Accumulated UniswapV2Tokens per share, times 1e12. See below.
    }
    // The UniswapV2Token TOKEN!
    IUniswapV2Token public rewardsToken;
    // UniswapV2Token tokens created per block.
    uint256 public rewardsPerBlock;
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when UniswapV2Token mining starts.
    uint256 public startBlock;
    uint256 public endBlock;
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        address _rewardsToken,
        uint256 _rewardsPerBlock,
        uint256 _startBlock,
        uint256 _endBlock
    ) {
        rewardsToken = IUniswapV2Token(_rewardsToken);
        rewardsPerBlock = _rewardsPerBlock;
        startBlock = _startBlock;
        endBlock = _endBlock;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock
            ? block.number
            : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accUniswapV2TokenPerShare: 0
            })
        );
    }

    // Update the given pool's UniswapV2Token allocation point. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(
        uint256 _from,
        uint256 _to
    ) public view returns (uint256) {
        if (_to <= endBlock) {
            return _to.sub(_from);
        } else if (_from >= endBlock) {
            return 0;
        } else {
            return endBlock.sub(_from);
        }
    }

    function pendingRewards(
        uint256 _pid,
        address _user
    ) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accUniswapV2TokenPerShare = pool.accUniswapV2TokenPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (
            block.number > pool.lastRewardBlock &&
            pool.lastRewardBlock < endBlock &&
            lpSupply != 0
        ) {
            uint256 multiplier = getMultiplier(
                pool.lastRewardBlock,
                block.number
            );
            uint256 uniswapV2TokenReward = multiplier
                .mul(rewardsPerBlock)
                .mul(pool.allocPoint)
                .div(totalAllocPoint);
            accUniswapV2TokenPerShare = accUniswapV2TokenPerShare.add(
                uniswapV2TokenReward.mul(1e12).div(lpSupply)
            );
        }
        return
            user.amount.mul(accUniswapV2TokenPerShare).div(1e12).sub(
                user.rewardDebt
            );
    }

    // Update reward vairables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        if ((block.number > endBlock) && (pool.lastRewardBlock > endBlock)) {
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 uniswapV2TokenReward = multiplier
            .mul(rewardsPerBlock)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);
        rewardsToken.mint(address(this), uniswapV2TokenReward);
        pool.accUniswapV2TokenPerShare = pool.accUniswapV2TokenPerShare.add(
            uniswapV2TokenReward.mul(1e12).div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to MasterChef for UniswapV2Token allocation.
    function deposit(uint256 _pid, uint256 _amount) public nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accUniswapV2TokenPerShare)
                .div(1e12)
                .sub(user.rewardDebt);
            safeUniswapV2TokenTransfer(msg.sender, pending);
        }
        pool.lpToken.safeTransferFrom(
            address(msg.sender),
            address(this),
            _amount
        );
        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accUniswapV2TokenPerShare).div(
            1e12
        );
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _amount) public nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user
            .amount
            .mul(pool.accUniswapV2TokenPerShare)
            .div(1e12)
            .sub(user.rewardDebt);
        safeUniswapV2TokenTransfer(msg.sender, pending);
        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accUniswapV2TokenPerShare).div(
            1e12
        );
        pool.lpToken.safeTransfer(address(msg.sender), _amount);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe UniswapV2Token transfer function, just in case if rounding error causes pool to not have enough UniswapV2Tokens.
    function safeUniswapV2TokenTransfer(address _to, uint256 _amount) internal {
        uint256 uniswapV2TokenBal = rewardsToken.balanceOf(address(this));

        if (_amount > uniswapV2TokenBal) {
            rewardsToken.transfer(_to, uniswapV2TokenBal);
        } else {
            rewardsToken.transfer(_to, _amount);
        }
    }

    // Revoke UniswapV2Token function, return onwership of token to origin
    function revokeUniswapV2Token(address _origin) external onlyOwner {
        Ownable(address(rewardsToken)).transferOwnership(_origin);
    }
}

contracts/pUSD3.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import {BEP20} from "./libraries/BEP20/BEP20.sol";
import {IBEP20} from "./interfaces/BEP20/IBEP20.sol";
import {IWBNB10} from "./interfaces/BEP20/IWBNB.sol";
import {IBEP4626} from "./libraries/BEP20/BEP4626.sol";
import {TransferHelper} from "./libraries/uniswapV2/TransferHelper.sol";
import {AggregatorV3Interface} from "./interfaces/AggregatorV3Interface.sol";

/// Deposit, Withdraw, Mint, Redeem exists for Stable.
/// Fund & Defund exists for Volatility.
/// All reserve asset accounting (WBNB) is done inside of Stable Vault. There is only one accounting.
/// Preserves 4626 expected interface (eg vault mint/burn operating on one underlying).

contract VolatilityToken is BEP20 {
    constructor() BEP20("Volatility Token", "VOLT", 18) {}

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external {
        _burn(from, amount);
    }
}

contract pUSD3StableVault is IBEP4626, BEP20 {
    using TransferHelper for BEP20;

    uint256 public volatilityBuffer;
    uint256 public constant depositFee = 100; // 0.1%
    uint256 public constant withdrawFee = 9500; // 99.5%
    uint256 public constant maxFloatFee = 10000; // 100%

    VolatilityToken public immutable volt;
    IWBNB10 public immutable wbnb;
    AggregatorV3Interface internal immutable priceFeed;

    event Withdraw(
        address from,
        address to,
        uint256 amountReserve,
        uint256 wbnbOut
    );

    constructor() BEP20("PUSD Stable Vault", "PUSD", 18) {
        volt = new VolatilityToken();
        wbnb = IWBNB10(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        priceFeed = AggregatorV3Interface(
            0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419
        );
    }

    /// @notice Stablecoin
    /// Give WBNB amount, get STABLE amount
    function deposit(
        uint256 wbnbIn,
        address to
    ) public override returns (uint256 stableCoinAmount) {
        require(
            (stableCoinAmount = previewDeposit(wbnbIn)) != 0,
            "ZERO_SHARES"
        );
        require(wbnb.transferFrom(msg.sender, address(this), wbnbIn));
        _mint(to, stableCoinAmount);
        emit Deposit(msg.sender, to, wbnbIn, stableCoinAmount);
        afterDeposit(wbnbIn);
    }

    /// @notice Stablecoin
    /// Mint specific AMOUNT OF STABLE by giving WBNB
    function mint(
        uint256 stableCoinAmount,
        address to
    ) public override returns (uint256 wbnbIn) {
        require(
            wbnb.transferFrom(
                msg.sender,
                address(this),
                wbnbIn = previewMint(stableCoinAmount)
            )
        );
        _mint(to, stableCoinAmount);
        emit Deposit(msg.sender, to, wbnbIn, stableCoinAmount);
        afterDeposit(wbnbIn);
    }

    /// @notice Stablecoin
    /// Withdraw from Vault underlying. Amount of WBNB by burning equivalent amount of STABLECOIN
    function withdraw(
        uint256 amountReserve,
        address to,
        address from
    ) public override returns (uint256 wbnbOut) {
        uint256 allowed = allowance[from][msg.sender];
        if (msg.sender != from && allowed != type(uint256).max)
            allowance[from][msg.sender] = allowed - amountReserve;
        wbnbOut = (previewWithdraw(amountReserve) * withdrawFee) / maxFloatFee;
        _burn(from, amountReserve);
        emit Withdraw(from, to, amountReserve, wbnbOut);
        wbnb.transferFrom(address(this), msg.sender, wbnbOut);
    }

    /// @notice Stablecoin
    /// Redeem from Vault underlying. (WBNB) equivalent to AMOUNTSTABLE
    function redeem(
        uint256 amountStable,
        address to,
        address from
    ) public override returns (uint256 wbnbOut) {
        uint256 allowed = allowance[from][msg.sender];
        if (msg.sender != from && allowed != type(uint256).max)
            allowance[from][msg.sender] = allowed - amountStable;
        require((wbnbOut = previewRedeem(amountStable)) != 0, "ZERO_ASSETS");
        wbnbOut = (previewRedeem(amountStable) * withdrawFee) / maxFloatFee;
        _burn(from, amountStable);
        emit Withdraw(from, to, wbnbOut, amountStable);
        wbnb.transferFrom(address(this), msg.sender, wbnbOut);
    }

    /// @notice Volatility/Funding token
    /// Give amount of WBNB, receive VolatilityToken
    function fund(
        uint256 volCoinAmount,
        address to
    ) public returns (uint256 wbnbIn) {
        require(
            wbnb.transferFrom(
                msg.sender,
                address(this),
                wbnbIn = previewFund(volCoinAmount)
            )
        );
        volt.mint(to, volCoinAmount);
        volatilityBuffer += wbnbIn;
        emit Deposit(msg.sender, to, wbnbIn, volCoinAmount);
    }

    /// @notice Volatility/Funding token
    /// Redeem number of SHARES (VolToken) for WBNB at current price (at loss or profit) + fees accrued
    function defund(
        uint256 volCoinAmount,
        address to,
        address from
    ) public returns (uint256 wbnbOut) {
        require((wbnbOut = previewDefund(volCoinAmount)) != 0, "ZERO_ASSETS");
        volt.burn(to, volCoinAmount);
        volatilityBuffer -= wbnbOut;
        wbnb.transferFrom(address(this), msg.sender, wbnbOut);
        emit Withdraw(from, to, wbnbOut, volCoinAmount);
    }

    function previewFund(
        uint256 amount
    ) public view returns (uint256 stableVaultShares) {
        return amount / (getLatestPrice() / 1e8); // AMOUNT / (ETH/USD)
    }

    /// @notice Volatility token
    /// The only function that claims yield from Vault
    /// https://jacob-eliosoff.medium.com/a-cheesy-analogy-for-people-who-find-usm-confusing-1fd5e3d73a79
    function previewDefund(
        uint256 amount
    ) public view returns (uint256 wbnbOut) {
        uint256 sharesGrowth = (amount *
            (volatilityBuffer * (getLatestPrice() / 1e8))) / volt.totalSupply();
        wbnbOut = sharesGrowth / (getLatestPrice() / 1e8);
    }

    /// @notice Stablecoin
    /// Return how much STABLECOIN does user receive for AMOUNT of WBNB
    function previewDeposit(
        uint256 amount
    ) public view override returns (uint256 stableCoinAmount) {
        return (getLatestPrice() / 1e8) * amount; // (ETH/USD) * AMOUNT
    }

    /// @notice Stablecoin
    /// Return how much WBNB is needed to receive AMOUNT of STABLECOIN
    function previewMint(
        uint256 amount
    ) public view override returns (uint256 stableCoinAmount) {
        return amount / (getLatestPrice() / 1e8); // AMOUNT / (ETH/USD)
    }

    /// @notice Stablecoin
    /// Return how much WBNB to transfer by calculating equivalent amount of burn to given AMOUNT of WBNB
    function previewWithdraw(
        uint256 amount
    ) public view override returns (uint256 wbnbOut) {
        return (getLatestPrice() / 1e8) * amount; // AMOUNT * (ETH/USD)
    }

    /// @notice Stablecoin
    /// Return how much WBNB to transfer equivalent to given AMOUNT of STABLECOIN
    function previewRedeem(
        uint256 amount
    ) public view override returns (uint256 wbnbOut) {
        return amount / (getLatestPrice() / 1e8); // AMOUNT / (ETH/USD)
    }

    /// @notice Add fee from user WBNB collateral to volatilityBuffer as FUNDers fee
    function afterDeposit(uint256 amount) internal {
        uint256 fee = (amount * depositFee) / maxFloatFee;
        volatilityBuffer += fee;
    }

    function beforeWithdraw(uint256 amount) internal {}

    function totalAssets() public view override returns (uint256) {
        return wbnb.balanceOf(address(this));
    }

    function assetsOf(address user) public view returns (uint256) {
        return balanceOf[user];
    }

    function voltAssetsOf(address user) public view returns (uint256) {
        return volt.balanceOf(user);
    }

    function assetsPerShare() public view returns (uint256) {
        return previewRedeem(10 ** decimals);
    }

    function maxDeposit(address) public pure override returns (uint256) {
        return type(uint256).max;
    }

    function maxMint(address) public pure override returns (uint256) {
        return type(uint256).max;
    }

    function maxWithdraw(address user) public view override returns (uint256) {
        return assetsOf(user);
    }

    function maxRedeem(address user) public view override returns (uint256) {
        return balanceOf[user];
    }

    function getLatestPrice() public view returns (uint256) {
        (, int256 price, , , ) = priceFeed.latestRoundData();
        return uint256(price);
    }
}

